import React from 'react';
import { Typography, Container, Card, CardContent, Box } from '@mui/material';

const payments = [
  { from: 'Alice', to: 'Bob', amount: 500, currency: 'INR' },
  { from: 'Charlie', to: 'Alice', amount: 300, currency: 'INR' },
];

function Payments() {
  return (
    <Container>
      <Typography variant="h4" sx={{ mt: 4, mb: 2 }} color="primary">
        Payments
      </Typography>
      <Box sx={{ display: 'flex', gap: 3, flexWrap: 'wrap' }}>
        {payments.map((p, idx) => (
          <Card key={idx} sx={{ minWidth: 220, mb: 2, bgcolor: 'background.paper', boxShadow: 3 }}>
            <CardContent>
              <Typography variant="h6" color="secondary">{p.from} â†’ {p.to}</Typography>
              <Typography variant="body2">Amount: {p.amount} {p.currency}</Typography>
            </CardContent>
          </Card>
        ))}
      </Box>
    </Container>
  );
}

export default Payments;
