import React, { useState } from 'react';
import { Typography, Container, Box, TextField, Button } from '@mui/material';
import CartoonAvatars from '../components/CartoonAvatars';

const initialGroups = [
  { name: 'Trip to Goa', members: 4 },
  { name: 'Project Team', members: 3 },
  { name: 'Family', members: 5 },
];

function Groups() {
  const [groups, setGroups] = useState(initialGroups);
  const [groupName, setGroupName] = useState('');
  const [memberCount, setMemberCount] = useState(1);

  const handleAddGroup = (e) => {
    e.preventDefault();
    if (groupName && memberCount > 0) {
      setGroups([...groups, { name: groupName, members: memberCount }]);
      setGroupName('');
      setMemberCount(1);
    }
  };

  return (
    <Container>
      <Typography variant="h4" sx={{ mt: 4, mb: 2 }} color="primary">
        Groups
      </Typography>
      <Box component="form" onSubmit={handleAddGroup} sx={{ mb: 4, display: 'flex', gap: 2 }}>
        <TextField label="Group Name" value={groupName} onChange={e => setGroupName(e.target.value)} required />
        <TextField label="Members" type="number" value={memberCount} onChange={e => setMemberCount(Number(e.target.value))} required inputProps={{ min: 1 }} />
        <Button type="submit" variant="contained" color="secondary">Add Group</Button>
      </Box>
      {groups.map((group, idx) => (
        <Box key={idx} sx={{ mb: 4, p: 2, bgcolor: 'background.paper', borderRadius: 2, boxShadow: 3 }}>
          <Typography variant="h6" color="secondary">{group.name}</Typography>
          <CartoonAvatars count={group.members} />
        </Box>
      ))}
    </Container>
  );
}

export default Groups;
